colors = [colors1,colors2,colors3,colors4,colors5,colors6,colors7,colors8]#,colors9,colors10]

class AugMixDataset(torch.utils.data.Dataset):
  """Dataset wrapper to perform AugMix augmentation."""

  def __init__(self, dataset, preprocess, no_jsd=False):
    self.dataset = dataset
    self.preprocess = preprocess
    self.no_jsd = no_jsd
    
  def __getitem__(self, i):
    np.random.seed()
    x, y = self.dataset[i]
    alpha=5e-3
    if self.no_jsd == True:
      x_original = self.preprocess(x.copy())
      idx1 = 0
      idx2 = 1
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      # m = torch.from_numpy(np.random.randint(0,2, (3,32,32)))
      mixed1 = ((1 - m) * x1 + m * x2).float()

      idx1 = 2
      idx2 = 3
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      # m = torch.from_numpy(np.random.randint(0,2, (3,32,32)))
      mixed2 = ((1 - m) * x1 + m * x2).float()

      idx1 = 4
      idx2 = 5
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      # m = torch.from_numpy(np.random.randint(0,2, (3,32,32)))
      mixed3 = ((1 - m) * x1 + m * x2).float()

      idx1 = 6
      idx2 = 7
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      # m = torch.from_numpy(np.random.randint(0,2, (3,32,32)))
      mixed4 = ((1 - m) * x1 + m * x2).float()

      # idx1 = 8
      # idx2 = 9
      # x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      # x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      # m = np.random.beta(alpha, alpha)
      # # m = torch.from_numpy(np.random.randint(0,2, (3,32,32)))
      # mixed5 = ((1 - m) * x1 + m * x2).float()
      return x_original, mixed1, mixed2, mixed3, mixed4, mixed4, y
    else:
      mixed1 = None
      x_original = self.preprocess(x.copy())
      box = [[0,1], [2,3], [4,5], [6,7]]
      idx = np.random.randint(0,args.num_of_TTA)
      idx1 = box[idx][0]
      idx2 = box[idx][1]
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      sampled = ((1 - m) * x1 + m * x2).float()

      idx1 = 0
      idx2 = 1
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      mix1 = ((1 - m) * x1 + m * x2).float()

      idx1 = 2
      idx2 = 3
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      mix2 = ((1 - m) * x1 + m * x2).float()

      idx1 = 4
      idx2 = 5
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      mix3 = ((1 - m) * x1 + m * x2).float()

      idx1 = 6
      idx2 = 7
      x1 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx1], colors[idx1])).float()
      x2 = self.preprocess(pixel_based_encryption(deepcopy(x), invs[idx2], colors[idx2])).float()
      m = np.random.beta(alpha, alpha)
      mix4 = ((1 - m) * x1 + m * x2).float()

      return  x_original, sampled, mix1, mix2, mix3, mix4, y

  def __len__(self):
    return len(self.dataset)



